import React, { useState } from 'react';
// import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import './sidebar.css';

const Sidebar = () => {
  const [collapsed, setCollapsed] = useState(false);
  const [profileOpen, setProfileOpen] = useState(false);

  const toggleSidebar = () => {
    setCollapsed(!collapsed);
  };

  const toggleProfile = () => {
    setProfileOpen(!profileOpen);
  };

  return (
    <div className={`sidebar ${collapsed ? 'collapsed' : ''}`}>
      <div className="sidebar-header">
        <div className="sidebar-toggle" onClick={toggleSidebar}>
          <i className={`fas ${collapsed ? 'fa-bars' : 'fa-times'}`}></i>
        </div>
        {!collapsed && <div className="sidebar-title">MAFIA</div>}
      </div>
      {!collapsed && (
        <div className="sidebar-menu">
          <div className="sidebar-menu-item active" onClick={toggleProfile}>
            <div className="profile-avatar">
              <i className="fas fa-user-circle"></i>
            </div>
            {profileOpen && (
              <div className="profile-info">
                <div className="profile-name">Имя пользователя</div>
                <div className="profile-edit">
                  <a href="/edit-profile">Редактировать профиль</a>
                </div>
              </div>
            )}
          </div>
          <div className="sidebar-menu-item active">
            <i className="fas fa-home"></i>
            <span>Главная</span>
          </div>
          <div className="sidebar-menu-item active">
            <i className="fas fa-users"></i>
            <span>Пользователи</span>
          </div>
          <div className="sidebar-menu-item active">
            <i className="fas fa-building"></i>
            <span>Компании</span>
          </div>
          <div className="sidebar-menu-item active">
            <i className="fas fa-money-bill-wave"></i>
            <span>Доход</span>
          </div>
          <div className="sidebar-menu-item active">
            <i className="fas fa-cog"></i>
            <span>Настройки</span>
          </div>
          <div className="sidebar-menu-item active">
            <i className="fas fa-user-lock"></i>
            <span>Роли и права</span>
          </div>
        </div>
      )}
    </div>
  );
};

export default Sidebar;
